import React, { useState } from 'react';
import { Row, Col, Search, Paper, Card, Text } from 'ui';

interface PagesDataProps {
  pagesData: {
    id: number;
    slug: string;
    data: {
      title: string;
      content: string;
    };
  }[];
}

export default function App({ pagesData }: PagesDataProps) {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (searchTerm: string) => {
    const results = searchPages(searchTerm);
    setSearchResults(results);
  };

  const searchPages = (term: string) => {
    const results = pagesData.filter((page) => {
      const { title, content } = page.data;
      const lowerCaseTerm = term.toLowerCase();
      return (
        title.toLowerCase().includes(lowerCaseTerm) ||
        content.toLowerCase().includes(lowerCaseTerm)
      );
    });
    return results.map((result) => result.data);
  };

  return (
    <Paper
      sticky
      header={ <Search onSearch={handleSearch} placeholder='Placeholder for Search' /> }
    >
      {searchResults.length > 0 ? (
        <Row gap={10} align='stretch'>
          {searchResults.map((result) => (
            <Col sm={12} md={8} lg={6} key={result.id}>
              <Card header={<Text type='title'>{result.title}</Text>}>
                <Text type='paragraph' size='medium'>{result.content}</Text>
              </Card>
            </Col>
          ))}
        </Row>
      ) : (
        <Text type='paragraph' align='center'>No Data</Text>
      )}
    </Paper>
  )
}
